mapper.select:
select di.*, ca.name as categoryName from dish di left join category ca on di.category_id = ca.id
and di.name like concat('%', #{name}, '%')
and di.category_id = #{categoryId}
and di.status = #{status}
order by di.create_time desc

mapper.update:
UPDATE dish
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, image = #{image}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}
